import 'dart:async';import 'package:flutter/material.dart';class StartScreen extends StatefulWidget {  @override  _StartScreenState createState() => _StartScreenState();}class _StartScreenState extends State<StartScreen> {  @override  void initState() {    navigatePage();    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.white,      body: Center(        child: Text(          'Employee Db',          style: TextStyle(color: Colors.black, fontSize: 25),        ),      ),    );  }  void navigatePage() async {    Timer(        const Duration(seconds: 3),        () => Navigator.pushNamedAndRemoveUntil(            context, "/home", (Route<dynamic> route) => false));  }}